import React, { Component } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';

class data extends Component {

    constructor(props) {
        super(props);
        this.state = {studData: []};
        this.remove = this.remove.bind(this);
    }

    componentDidMount() {
        fetch('/vaccinestatus')
            .then(response => response.json())
            .then(data => this.setState({studData: data}));
    }

    async remove(id) {
        await fetch(`/vaccinestatus/${id}`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
            let updatedData = [...this.state.studData].filter(i => i.id !== id);
            this.setState({studData: updatedData});
        });
    }
    
    render() {
        const {studData, isLoading} = this.state;

        if(isLoading){
            return <p>Loading...</p>
        }
        
        const studentList = studData.map(student => {
            Object.entries(student).forEach(prop => console.log(student[prop]));    
            
            return <tr key={student.id}>
            <td style={{whiteSpace: 'nowrap'}}>{student.name}</td>
            <td>{student.branch}</td>
            <td>{student.yearOfStudy}</td>
            <td>{student.vaccinationName}</td>
            <td>{student.firstVaccinationDate}</td>
            <td>{student.secondVaccinationDate}</td>
            <td>
                <ButtonGroup>
                <Button size="sm" color="primary" tag={Link} to={"/vaccinestatus/" + student.id}>Edit</Button>
                <Button size="sm" color="danger" onClick={() => this.remove(student.id)}>Delete</Button>
                </ButtonGroup>
            </td>
            </tr>
        });
        
        return (  
            <div>
                <AppNavbar/>
                <Container fluid>
                    <div className="float-right">
                        <Button color="success" tag={Link} to="/vaccinestatus/new">Add student data</Button>
                    </div>
                    <br></br>
                    <h3>Student Data</h3>
                    <Table className="mt-7">
                        <thead>
                        <tr>
                            <th>Name</th>
                            <th>Branch</th>
                            <th>Year Of Study</th>
                            <th>vaccination Name</th>
                            <th>First Vaccination Date</th>
                            <th>Second Vaccination Date</th>
                            <th>Actions</th>
                        </tr>
                        </thead>
                        <tbody>
                        {studentList}
                        </tbody>
                    </Table>
                </Container>
            </div>
        );
    }
}
export default data;
