import logo from './logo.svg';
import './App.css';
import { Component } from 'react';

class App extends Component {
  state = {
    studData : []
  };

  async componentDidMount() {
    const response = await fetch('/vaccinestatus');
    const body = await response.json();
    this.setState({studData: body});
  }

  async remove(id) {
    await fetch(`/vaccinestatus/${id}`, {
        method: 'DELETE',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        }
    }).then(() => {
        let updatedData = [...this.state.studData].filter(i => i.id !== id);
        this.setState({studData: updatedData});
    });
  }

  render() {
    const {studData, isLoading} = this.state;

    if(isLoading){
      return <p>Loading...</p>
    }

    const studentList = studData.map(student => {
      return <tr key={student.id}>
        <td style={{whiteSpace: 'nowrap'}}>{student.name}</td>
        <td>{student.branch}</td>
        <td>{student.yearOfStudy}</td>
        <td>{student.vaccinationName}</td>
        <td>{student.firstVaccinationDate}</td>
        <td>{student.secondVaccinationDate}</td>
        <td>
          <ButtonGroup>
            <Button size="sm" color="primary" tag={Link} to={"/vaccinestatus/" + student.id}>Edit</Button>
            <Button size="sm" color="danger" onClick={() => this.remove(student.id)}>Delete</Button>
          </ButtonGroup>
        </td>
      </tr>
    });

    return (  
      // <div className="App">
      //   <header className="App-header">
      //     <img src={logo} className="App-logo" alt="logo" />
      //     <p>
      //       Edit <code>src/App.js</code> and save to reload.
      //     </p>
      //     <a
      //       className="App-link"
      //       href="https://reactjs.org"
      //       target="_blank"
      //       rel="noopener noreferrer"
      //     >
      //       Learn React
      //     </a>
      //     <h2>Student Data</h2>
      //     {studData.map(student =>
      //       <div>{student.name} {student.vaccinationName}</div>
      //     )}
      //   </header>
      // </div>
      <div>
          <AppNavbar/>
          <Container fluid>
              <div className="float-right">
                  <Button color="success" tag={Link} to="/vaccinestatus/new">Add student data</Button>
              </div>
              <h3>Student Data</h3>
              <Table className="mt-4">
                  <thead>
                  <tr>
                      <th>Name</th>
                      <th>Branch</th>
                      <th>Year Of Study</th>
                      <th>vaccination Name</th>
                      <th>First Vaccination Date</th>
                      <th>Second Vaccination Date</th>
                      <th>Actions</th>
                  </tr>
                  </thead>
                  <tbody>
                  {studentList}
                  </tbody>
              </Table>
          </Container>
      </div>
    );
  }
}

export default App;
